/** * NetSuite preferences *  * Version  Date            Author          Remarks * 1.00     11/28/2013      pmiller         Initial version * 2.00     01/20/2014      pbtan           updated to remove the leading 0's * 3.00     05/21/2014      pbtan           added new date format * 4.00     06/17/2014      maquino         added new date format for YYYY"å¹´"MM"æœˆ"DD"æ—¥" * 5.00     06/17/2014      maquino         changed encoding to UTF-8 * 6.00     06/17/2014      pbtan           added new date formats * 7.00     06/17/2014      pmiller         updated DD.MMM YYYY to DD. MON YYYY * 8.00     06/17/2014      pbtan           added space to the format * 9.00     06/18/2014      pbtan           updated a format. *  */function convertNSDateFormat() {    // convert NS format to ExtJS notation    var dateFormat = 'Y/n/j';    switch (nlapiGetContext().getPreference('dateformat')) {        case 'MM/DD/YYYY':            dateFormat = 'n/j/Y';            break;        case 'DD/MM/YYYY':            dateFormat = 'j/n/Y';            break;        case 'DD-Mon-YYYY':            dateFormat = 'j-M-Y';            break;        case 'DD.MM.YYYY':            dateFormat = 'j.n.Y';            break;        case 'DD-MONTH-YYYY':            dateFormat = 'j-F-Y';            break;        case 'DD MONTH, YYYY':            dateFormat = 'j F, Y';            break;        case 'YYYY/MM/DD':            dateFormat = 'Y/n/j';            break;        case 'YYYY-MM-DD':            dateFormat = 'Y-n-j';            break;        case 'YYYY MM DD':            dateFormat = 'Y n j';            break;        case 'YYYY"年"MM"月"DD"日"': // chinese/ japanese            dateFormat = 'Y年n月j日';            break;        case 'DD MONTH YYYY': // czech        case 'DD MMMM YYYY': // french            dateFormat = 'j F Y';            break;        case 'DD de MMMM de YYYY': // spanish        case 'DD "de" MONTH "de" YYYY': // spanish            dateFormat = 'j \\de F \\de Y'; // double backslash as escape character            break;        case 'YYYY"년" MM"월" DD"일"': // spanish            dateFormat = 'Y년 n월 j일'; // korean            break;        case 'DD. MMM YYYY': // german            dateFormat = 'j. M Y';            break;    }    return dateFormat;};function convertNSLongDateFormat(view) {    var longDateFormat = 'D M d';    switch (nlapiGetContext().getPreference('longdateformat')) {        case 'Month DD, YYYY':            if (view == 'daily') longDateFormat = 'D M j';            if (view == 'weekly') longDateFormat = 'M j';            if (view == 'monthly') longDateFormat = 'M Y';            break;        case 'DD Month YYYY':            if (view == 'daily') longDateFormat = 'D j M';            if (view == 'weekly') longDateFormat = 'j M';            if (view == 'monthly') longDateFormat = 'M Y';            break;        case 'YYYY Month DD':            if (view == 'daily') longDateFormat = 'D M j';            if (view == 'weekly') longDateFormat = 'M j';            if (view == 'monthly') longDateFormat = 'Y M';            break;        default:            if (view == 'daily') longDateFormat = 'D M j';            if (view == 'weekly') longDateFormat = 'M j';            if (view == 'monthly') longDateFormat = 'M Y';            break;    }    return longDateFormat;}function isNumber(n) {    return !isNaN(parseFloat(n)) && isFinite(n);};function getNSFontStyle() {    return nlapiGetContext().getPreference('font');}